INSERT INTO `diendan` (`MaBV`, `TieuDe`, `Mota`, `Baiviet`, `chuyenmuc`, `MaUser`, `ngaydang`) VALUES
('001', 'Cách Full Điểm phần Thi Listening', 'em cần tips full điểm listening ', '<p>&uacute;m ba la alaba trap</p>', 'hoidap', 'hvtest', '10/28/22'),
('002', 'Chia sẻ kinh nghiệm của mình về quá trình học ngữ pháp', 'Đây là cách học giúp Trúc đạt được full điểm Reading', '<h2 class=\"has-text-align-center\" style=\"box-sizing: border-box; color: #0a0a0a; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.6em; line-height: 1.3; font-family: Roboto, sans-serif; text-align: center; background-color: #ffffff;\">4 Bước Học Ngữ Ph&aacute;p Tiếng Anh C&oacute; Hiệu Quả</h2>\r\n<h3 style=\"box-sizing: border-box; color: #0a0a0a; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em; font-family: Roboto, sans-serif; background-color: #ffffff;\">#1. Học c&oacute; mục đ&iacute;ch r&otilde; r&agrave;ng</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">L&agrave;m bất kể điều g&igrave; nhỏ nhất bạn cũng đều c&oacute; mục đ&iacute;ch. Với kiến thức ngữ ph&aacute;p cũng vậy, ngay cả người bản ngữ cũng kh&oacute; l&ograve;ng học hết khối khổng lồ kiến thức đ&oacute;&nbsp;n&ecirc;n để th&agrave;nh c&ocirc;ng bạn phải biết chọn lọc ra đ&acirc;u l&agrave; mục ti&ecirc;u của m&igrave;nh v&agrave; chọn&nbsp;kiến thức ph&ugrave; hợp.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">Viết ra một bản mục ti&ecirc;u h&agrave;nh động r&otilde; r&agrave;ng bằng c&aacute;ch trả lời c&aacute;c c&acirc;u hỏi:</p>\r\n<ul style=\"box-sizing: border-box; list-style-position: initial; list-style-image: initial; margin-top: 0px; padding: 0px; margin-bottom: 1.3em; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Bạn sẽ l&agrave;m g&igrave; nếu đ&atilde; vững lượng kiến thức ngữ ph&aacute;p n&agrave;y?</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Nếu kh&aacute; hơn, bạn&nbsp;sẽ trở th&agrave;nh người như thế n&agrave;o?</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Bạn chắc sẽ ho&agrave;n th&agrave;nh kế hoạch trong v&ograve;ng 6 / 9 / 12 th&aacute;ng?</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Những g&igrave; cần phải loại bỏ khỏi, cắt giảm khỏi cuộc sống của bạn để tập trung v&agrave;o việc học nhiều hơn?</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">Với một v&agrave;i c&acirc;u hỏi tr&ecirc;n, h&atilde;y trả lời c&agrave;ng chi tiết c&agrave;ng tốt v&agrave; l&agrave;m bản mục ti&ecirc;u d&aacute;n l&ecirc;n g&oacute;c học tập của m&igrave;nh. Điều n&agrave;y sẽ l&agrave; động lực rất lớn cho bạn mỗi khi gặp kh&oacute; khăn v&agrave; muốn bỏ cuộc.</p>\r\n<h3 style=\"box-sizing: border-box; color: #0a0a0a; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em; font-family: Roboto, sans-serif; background-color: #ffffff;\">#2. Chia nhỏ mục ti&ecirc;u học tập theo từng phần nhỏ</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">Mục ti&ecirc;u lớn l&agrave; điều đương nhi&ecirc;n, nhưng để thực hiện được mục ti&ecirc;u lớn đ&oacute;, bạn cần x&acirc;y dựng th&agrave;nh c&aacute;c mục ti&ecirc;u nhỏ, nhỏ hơn.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">Chẳng hạn, muốn l&agrave;m trong một&nbsp;<a style=\"box-sizing: border-box; background-color: transparent; touch-action: manipulation; text-decoration-line: none;\" href=\"https://engbreaking.com/phong-van-tieng-anh/\">c&ocirc;ng ty</a>&nbsp;li&ecirc;n doanh với người nước ngo&agrave;i, kh&ocirc;ng chỉ l&agrave; ngữ ph&aacute;p tiếng Anh, bạn c&ograve;n phải đi song song cả kỹ năng giao tiếp. Muốn l&agrave;m được điều n&agrave;y, h&atilde;y chia cung thời gian của m&igrave;nh theo hướng khoa học hơn.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><span style=\"box-sizing: border-box; font-weight: 600;\">V&iacute; dụ:</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><em style=\"box-sizing: border-box;\">Mục ti&ecirc;u:</em>&nbsp;Phải nghe được tất cả những th&ocirc;ng tin trong cuộc giao tiếp cơ bản h&agrave;ng ng&agrave;y.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><em style=\"box-sizing: border-box;\">Kế hoạch:</em></p>\r\n<ul style=\"box-sizing: border-box; list-style-position: initial; list-style-image: initial; margin-top: 0px; padding: 0px; margin-bottom: 1.3em; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Luyện nghe 30 ph&uacute;t mỗi ng&agrave;y.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">T&iacute;ch cực tham gia giao tiếp với người nước ngo&agrave;i.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Li&ecirc;n tục tự học, tự tạo m&ocirc;i trường tiếng Anh quanh m&igrave;nh.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">\r\n<h3 style=\"box-sizing: border-box; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em;\">#3. Tập trung v&agrave;o từng khối ngữ ph&aacute;p li&ecirc;n quan</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Đừng học lan man, học tập trung v&agrave;o chủ đề&nbsp;m&igrave;nh quan t&acirc;m l&agrave; c&aacute;ch s&aacute;ng suốt nhất nếu bạn muốn tiến tới đ&iacute;ch nhanh hơn.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Điều n&agrave;y gi&uacute;p bạn c&oacute; thể tự học v&agrave; tự thực h&agrave;nh nhiều nhất bởi đ&oacute; l&agrave; những thứ bạn cần, &eacute;p m&igrave;nh phải biết. V&agrave; chỉ đến khi học nhuần nhuyễn, c&oacute; thể dạy cho người kh&aacute;c mới chuyển sang học nội dung kh&aacute;c.</p>\r\n<h3 style=\"box-sizing: border-box; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em;\">#4. Học mới v&agrave; &ocirc;n luyện đều đặn h&agrave;ng ng&agrave;y</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Chỉ từ 30 ph&uacute;t đến 1 tiếng&nbsp;<a style=\"box-sizing: border-box; background-color: transparent; touch-action: manipulation; text-decoration-line: none;\" href=\"https://engbreaking.com/hoc-tieng-anh-moi-ngay\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\" (opens in a new tab)\">mỗi ng&agrave;y</a>&nbsp;nhưng l&agrave; tập trung &ocirc;n luyện, học nội dung mới đều đặn l&agrave; bạn đều c&oacute; thể tiếp thu rất nhanh, đạt mục ti&ecirc;u đ&uacute;ng kế hoạch.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Nhưng sự ki&ecirc;n tr&igrave; bền bỉ n&agrave;y &iacute;t ai c&oacute; thể theo kịp v&igrave; ch&uacute;ng ta lu&ocirc;n bị mắc phải những c&aacute;m dỗ th&uacute; vị hơn ngo&agrave;i thực tại. Đơn giản đ&ocirc;i ba ph&uacute;t lướt facebook, xem quần &aacute;o,.. rồi bạn sẽ dần lấn th&agrave;nh nửa tiếng, 1 giờ, 2 giờ, thậm ch&iacute; cả buổi tối.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Thay đổi được th&oacute;i quen n&agrave;y ng&agrave;y n&agrave;o, bạn sẽ thấy cuộc sống c&oacute; &yacute; nghĩa hơn rất nhiều ng&agrave;y đ&oacute;. H&atilde;y thử một trong những phương ph&aacute;p sau:</p>\r\n<ul style=\"box-sizing: border-box; list-style: disc; margin-top: 0px; padding: 0px; margin-bottom: 1.3em;\">\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Nghe nhạc, nghe podcast tiếng Anh</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Xem phim hoạt h&igrave;nh tiếng Anh</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Đọc s&aacute;ch</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Luyện viết</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Tham gia c&acirc;u lạc bộ tiếng Anh</li>\r\n</ul>\r\n<h2 class=\"has-text-align-center\" style=\"box-sizing: border-box; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.6em; line-height: 1.3; text-align: center;\">3 Mức Ngữ Ph&aacute;p Cần Phải Ho&agrave;n Th&agrave;nh</h2>\r\n<h3 style=\"box-sizing: border-box; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em;\">Ngữ ph&aacute;p mức &ldquo;Từ&rdquo;</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Mỗi một từ vựng trong tiếng Anh đều c&oacute; những tr&aacute;ch nhiệm kh&aacute;c nhau. Để c&oacute; thể n&oacute;i th&agrave;nh c&acirc;u, th&agrave;nh b&agrave;i diễn&nbsp;thuyết tốt th&igrave; bạn phải tốt ngay từ bước từ ri&ecirc;ng.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Cũng như tiếng Việt, tiếng Anh c&oacute; rất nhiều dạng từ vựng, ch&uacute;ng c&oacute; thể l&agrave; danh từ, đại từ,&nbsp;<a style=\"box-sizing: border-box; background-color: transparent; touch-action: manipulation; text-decoration-line: none;\" href=\"https://engbreaking.com/tinh-tu-trong-tieng-anh/\">t&iacute;nh từ</a>, động từ, ph&oacute; từ, giới từ, trạng từ&hellip; Khi kh&aacute;m ph&aacute; được hết c&aacute;c quy tắc bổ sung của c&aacute;c kiểu từ tr&ecirc;n l&agrave; bạn đ&atilde; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ lớn của m&igrave;nh.</p>\r\n<h3 style=\"box-sizing: border-box; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em;\">Ngữ ph&aacute;p mức &ldquo;C&acirc;u&rdquo;</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Phức tạp dần l&ecirc;n với mức c&acirc;u văn, đến đ&acirc;y y&ecirc;u cầu bạn phải c&oacute; sự kết hợp của nhiều loại từ, c&aacute;ch sử dụng kh&aacute;c nhau.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\"><span style=\"box-sizing: border-box; font-weight: 600;\">C&aacute;c điểm cần lưu &yacute; v&agrave; phải học:</span></p>\r\n<ul style=\"box-sizing: border-box; list-style: disc; margin-top: 0px; padding: 0px; margin-bottom: 1.3em;\">\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Hiểu cấu tr&uacute;c của một c&acirc;u đơn giản</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Sử dụng từ li&ecirc;n quan đến chủ đề theo vị tr&iacute; ch&iacute;nh x&aacute;c</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">N&ecirc;n học c&aacute;ch gh&eacute;p c&acirc;u</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">C&acirc;u điều kiện &ldquo;nếu &ndash; th&igrave;&rdquo; v&ocirc; c&ugrave;ng quan trọng trong</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Sử dụng mệnh đề để tạo c&acirc;u phức</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Ch&iacute;nh tả, dấu c&acirc;u</li>\r\n</ul>\r\n<h3 style=\"box-sizing: border-box; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em;\">Ngữ ph&aacute;p mức &ldquo;Đoạn&rdquo;</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Đoạn văn cơ bản thường sẽ chứa từ 3 đến 7 c&acirc;u. Mỗi đoạn sẽ c&oacute; một c&acirc;u chủ đề n&oacute;i chung nhất về nội dung của to&agrave;n đoạn v&agrave; những c&acirc;u kh&aacute;c l&agrave; r&otilde; &yacute; hơn cho c&acirc;u chủ đề n&agrave;y.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Khi kết hợp ngữ ph&aacute;p theo đoạn, bạn cần đặc biệt lưu &yacute; đến li&ecirc;n kết cấu tr&uacute;c c&aacute;c c&acirc;u. Th&igrave; trong đoạn phải nhất qu&aacute;n v&agrave; theo một mạch văn nhất định.</p>\r\n<h2 class=\"has-text-align-center\" style=\"box-sizing: border-box; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.6em; line-height: 1.3; text-align: center;\">C&aacute;c &ldquo;Tip&rdquo; Học Ngữ Ph&aacute;p Tiếng Anh Đ&aacute;ng Gi&aacute;</h2>\r\n<h3 style=\"box-sizing: border-box; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em;\">1. Kh&ocirc;ng qu&ecirc;n quy tr&igrave;nh Nghe &ndash; N&oacute;i &ndash; Đọc &ndash; Viết</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">D&ugrave; l&agrave; bất kỳ kỹ năng n&agrave;o trong tiếng Anh, bạn cũng đều cần c&oacute; sự thống nhất theo một quy tr&igrave;nh chuẩn. Điều n&agrave;y kh&ocirc;ng bắt buộc nhưng đ&oacute; l&agrave; lời khuy&ecirc;n tốt nhất nếu thực sự muốn ph&aacute;t triển khả năng của m&igrave;nh.</p>\r\n</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Ở mỗi qu&aacute; tr&igrave;nh bạn sẽ phải tinh tế nhận biết những kiến thức m&igrave;nh c&ograve;n thiếu v&agrave; củng cố kh&ocirc;ng ngừng.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Kh&ocirc;ng kh&oacute; nếu bạn tập trung học v&agrave; coi trọng từng cấu tr&uacute;c m&igrave;nh đ&atilde; đọc qua. Tuy nhi&ecirc;n kh&ocirc;ng phải ai cũng c&oacute; thể nhớ hết được ch&uacute;ng, v&igrave; vậy trong qu&aacute; tr&igrave;nh học y&ecirc;u cầu bạn phải ghi nhớ nhiều hơn bằng c&aacute;ch luyện tập đều đặn mỗi ng&agrave;y.</p>\r\n<h3 style=\"box-sizing: border-box; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em;\">2. Bắt đầu ngay với c&aacute;c th&igrave; tiếng Anh</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">D&ugrave; bạn học bất kỳ nội dung g&igrave;, kiến thức n&agrave;o th&igrave; vẫn phải tu&acirc;n thủ theo quy tắc của c&aacute;c th&igrave; tiếng Anh.V&igrave; vậy h&atilde;y bắt đầu đơn giản nhất với cấu tr&uacute;c c&aacute;c th&igrave;, học v&agrave; luyện theo thật r&otilde; r&agrave;ng mới chuyển đến c&aacute;c loại cấu tr&uacute;c kh&aacute;c.</p>\r\n</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">\r\n<h3 style=\"box-sizing: border-box; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em;\">3. T&igrave;m t&agrave;i liệu đ&aacute;ng tin cậy</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">C&oacute; qu&aacute; nhiều phương ph&aacute;p v&agrave; gi&aacute;o tr&igrave;nh chất lượng nhưng bạn kh&ocirc;ng biết phải chọn lựa ch&uacute;ng thế n&agrave;o.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">V&agrave; t&igrave;nh trạng n&agrave;y kh&ocirc;ng phải m&igrave;nh bạn mắc phải.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">Để t&igrave;m được một cuốn t&agrave;i liệu ph&ugrave; hợp, quan trọng nhất l&agrave; bạn biết m&igrave;nh đang ở tr&igrave;nh độ n&agrave;o.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px;\">H&atilde;y thử c&aacute;c b&agrave;i test miễn ph&iacute; tr&ecirc;n mạng để đ&aacute;nh gi&aacute; s&aacute;t nhất khả năng của bạn v&agrave; t&igrave;m hướng đi ph&ugrave; hợp.</p>\r\n<ul style=\"box-sizing: border-box; list-style: disc; margin-top: 0px; padding: 0px; margin-bottom: 1.3em;\">\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Nếu l&agrave; người mới bắt đầu, đ&atilde; mất gốc v&agrave; muốn học giao tiếp tốt, bạn c&oacute; thể lựa chọn ATI</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Cao l&ecirc;n một ch&uacute;t với c&ugrave;ng mục ti&ecirc;u giao tiếp bạn c&oacute; thể chọn Effortless English</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Bộ gi&aacute;o tr&igrave;nh nổi tiếng English Vocabulary in Use gi&uacute;p bạn ph&aacute;t triển hơn về từ vựng</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Cambridge IELTS l&agrave; bộ t&agrave;i liệu gối đầu cho người muốn luyện thi IELTS v&agrave; học ngữ ph&aacute;p tốt hơn.</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">Để tr&aacute;nh nh&agrave;m ch&aacute;n, bạn kh&ocirc;ng n&ecirc;n chỉ chọn t&agrave;i liệu tiếng Anh bằng c&aacute;c loại s&aacute;ch, t&agrave;i liệu văn bản m&agrave; cần kết hợp c&aacute;c t&agrave;i liệu dưới dạng podcast, audio, video,phim ảnh&nbsp;</p>\r\n<h3 style=\"box-sizing: border-box; color: #0a0a0a; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em; font-family: Roboto, sans-serif; background-color: #ffffff;\">4. Học về c&aacute;ch sắp xếp loại từ chuẩn x&aacute;c</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">C&aacute;ch sử dụng c&aacute;c loại từ vựng đều phải tu&acirc;n theo một quy tắc nhất định. Để c&oacute; được lượng kiến thức ngữ ph&aacute;p tiếng Anh ch&iacute;nh x&aacute;c nhất ngay từ đầu bạn phải hiểu được vị tr&iacute; của ch&uacute;ng trong c&acirc;u.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><span style=\"box-sizing: border-box; font-weight: 600;\">H&atilde;y nhớ:</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><em style=\"box-sizing: border-box;\">Vị tr&iacute; của danh từ:</em></p>\r\n<ul style=\"box-sizing: border-box; list-style-position: initial; list-style-image: initial; margin-top: 0px; padding: 0px; margin-bottom: 1.3em; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Danh&nbsp;từ đứng sau a, an, the, this, that, these, those&hellip;</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Danh từ đứng sau my, your, her, his&hellip;</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Danh từ đứng sau từ chỉ số lượng như many, some, any..</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><em style=\"box-sizing: border-box;\">Vị tr&iacute; của t&iacute;nh từ</em></p>\r\n<ul style=\"box-sizing: border-box; list-style-position: initial; list-style-image: initial; margin-top: 0px; padding: 0px; margin-bottom: 1.3em; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Sau động từ tobe</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Trước danh từ, bổ nghĩa cho danh từ</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Sau c&aacute;c từ chỉ nhận thức (look, feel, seem, smell, taste, find,&hellip;)</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Sau stay, remain, become</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><em style=\"box-sizing: border-box;\">Vị tr&iacute; của&nbsp;Trạng từ</em></p>\r\n<ul style=\"box-sizing: border-box; list-style-position: initial; list-style-image: initial; margin-top: 0px; padding: 0px; margin-bottom: 1.3em; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Đứng sau động từ thường</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Ở giữa trợ động từ v&agrave; động từ thường</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Sau động từ tobe</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Đứng trước hoặc sau động từ, bổ nghĩa cho động từ</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Đứng trước t&iacute;nh từ</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Đứng trước trạng từ, bổ nghĩa cho trạng từ</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><em style=\"box-sizing: border-box;\">Vị tr&iacute; của động từ:</em>&nbsp;Động từ thường đứng ngay sau chủ ngữ</p>\r\n<h3 style=\"box-sizing: border-box; color: #0a0a0a; width: 772.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.25em; font-family: Roboto, sans-serif; background-color: #ffffff;\">5. Những c&acirc;u ngắn, r&otilde; r&agrave;ng sẽ hiệu quả hơn rất nhiều</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">Người học tiếng Anh thường mắc phải một số lỗi khi viết th&agrave;nh c&acirc;u:</p>\r\n<ul style=\"box-sizing: border-box; list-style-position: initial; list-style-image: initial; margin-top: 0px; padding: 0px; margin-bottom: 1.3em; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">C&acirc;u qu&aacute; d&agrave;i ảnh hưởng đến trải nghiệm người đọc</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Mệnh đề chồng ch&eacute;o nhau nhưng kh&ocirc;ng kiểm so&aacute;t được vai tr&ograve; ngữ ph&aacute;p</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Bắt đầu c&acirc;u bằng c&aacute;c t&acirc;n ngữ, động từ</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Cấu tr&uacute;c c&acirc;u kh&ocirc;ng sắp xếp khoa học</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">C&acirc;u c&oacute; li&ecirc;n từ bắt đầu l&agrave; c&acirc;u c&oacute; ngữ ph&aacute;p chưa ho&agrave;n chỉnh</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Chưa r&otilde; r&agrave;ng giữa ăn phong n&oacute;i v&agrave; văn phong viết</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">Tập th&oacute;i quen viết c&acirc;u ho&agrave;n chỉnh sẽ gi&uacute;p mạch h&agrave;nh văn của bạn ng&agrave;y c&agrave;ng r&otilde; r&agrave;ng, mạch lạc hơn.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\">Thay v&igrave; sử dụng c&aacute;c mệnh đề chồng ch&eacute;o nhau, bạn c&oacute; thể t&aacute;ch ra th&agrave;nh nhiều c&acirc;u ngắn.&nbsp;Tuy nhi&ecirc;n, theo c&aacute;ch n&agrave;y trong trường hợp h&agrave;i hước, trong giao tiếp h&agrave;ng ng&agrave;y th&igrave; sẽ c&oacute; những nhược điểm nhất định. N&oacute; sẽ l&agrave;m mất thời gian của người nghe, giọng điệu kh&oacute; diễn tả theo hướng h&agrave;i hước, d&iacute; dỏm.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><span style=\"box-sizing: border-box; font-weight: 600;\">Đừng viết:</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><em style=\"box-sizing: border-box;\">&ldquo;Less smoking would undoubtedly lead to redundancies in the tobacco industry, a consequent rise in the number of unemployed, more people dependent upon State benefits to be supported by a government with subsequently reduced income.&rdquo;</em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><span style=\"box-sizing: border-box; font-weight: 600;\">H&atilde;y viết:</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 20px; color: #0a0a0a; font-family: Roboto, sans-serif; font-size: 18px; background-color: #ffffff;\"><em style=\"box-sizing: border-box;\">&ldquo;Less smoking would undoubtedly lead to redundancies in the tobacco industry and a consequent rise in the number of unemployed. More people would then become dependent upon State benefits, which would have to be paid out by a government with a reduced income.&rdquo;</em></p>', 'hoidap', 'HV_70241', '12/12/22'),
